import React, { Component } from 'react';
import gql from "graphql-tag";
import { graphql } from 'react-appolo';

const TodoQuery = gql`
  {
    todos{
      id
      text
      complete
    }
  }
`;

class App extends Component {
  render() {
    const { data: { loading, todos } } = this.props;
    return (
      <div>
        {!loading &&
          todos.map(todo => (
            <div>
              {todo.text}
            </div>
          ))
        }
      </div>
    )
  }
}

export default graphql(TodoQuery)(App);
