import React, { Component } from "react";
import gql from "graphql-tag";
import { graphql, compose } from "react-apollo";
import Paper from "@material-ui/core/Paper";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';

const TodosQuery = gql`
  {
    todos {
      id
      text
      complete
    }
  }
`;

const UpdateMutation = gql`
  mutation($id: ID!, $complete: Boolean!) {
    updateTodo(id: $id, complete: $complete)
  }
`;

const RemoveMutation = gql`
  mutation($id: ID!){
    removeTodo(id: $id)
  }
`;

class App extends Component {

 updateTodo = async todo =>  {
    console.log(this.props);
    await this.props.updateTodo({
      variables: {
        id: todo.id,
        complete: !todo.complete
      },
      update: store => {
        //Read data from cache to query
        const data = store.readQuery({ query: TodosQuery });
        data.todos = data.todos.map(
          task => (task.id === todo.id) ? {...todo, complete: !todo.complete} : task
        );

        store.writeQuery({ query: TodosQuery, data})
      }
    })
  };


  removeTodo = async (todo) => {
    await this.props.removeTodo({
      variables: {
        id: todo.id
      },
      remove: store => {
        const data = store.readQuery({ query: TodosQuery });
        data.todos = data.todos.filter()
      }
    })
  }

  render() {
    const {
      data: { loading, todos }
    } = this.props;
    return (
      <div style={{ display: "flex" }}>
        <div style={{ margin: "auto", width: "400px" }}>
          <Paper elavation={1}>
            {!loading && 
              <List>
              {todos.map(todo => (
                <ListItem key={todo.id} onClick={() => this.updateTodo(todo)}>
                  <Checkbox
                    checked={todo.complete}
                    tabIndex={-1}
                    disableRipple
                  />
                  <ListItemText primary={todo.text} />
                  <ListItemSecondaryAction>
                    <IconButton onClick={() => this.removeTodo(todo)}>
                      <CloseIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
            }
          </Paper>
        </div>
      </div>
    );
  }
}

export default compose(
  grapghQl(RemoveMutation, {name: "removeTodo"}),
  graphql(TodosQuery), 
  graphql(UpdateMutation, {name: "updateTodo"})
)(App);
